// Gigatron France for ST .. Partial Amiga The Band Cracktro !
#define t iTime*12.
float bars(vec2 q,float size) {
    return cos(q.y+t+q.y*-size);
}

float rand (in vec2 uv) { return fract(sin(dot(uv,vec2(12.4124,48.4124)))*48512.41241); }
const vec2 O = vec2(0.,1.);
float noise (in vec2 uv) {
	vec2 b = floor(uv);
	return mix(mix(rand(b),rand(b+O.yx),.5),mix(rand(b+O),rand(b+O.yy),.5),.5);
}

#define DIR_RIGHT -1.
#define DIR_LEFT 1.
#define DIRECTION DIR_LEFT

#define LAYERS 6
#define SPEED 140.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 q = uv;

    float stars = 0.;
	float fl, s;
	for (int layer = 0; layer < LAYERS; layer++) {
		fl = float(layer);
		s = (300.-fl*10.);
		stars += step(.1,pow(noise(mod(vec2(uv.x*s - iTime*SPEED*DIRECTION - fl*100.,uv.y*s),iResolution.x)),18.)) * (fl/float(LAYERS));
	}

     float ypos=0.4*abs(sin(t/5.));   
     float b = bars(q-ypos,45.);// size


    vec3 col   = vec3(smoothstep(0.0, 0.2, b));
         col.b = 0.9;
         col  *= smoothstep(.15, .02, abs(q.y -0.5-ypos));//- ypos));

         if(col.b<0.1) col  +=stars ;

	fragColor  = vec4( col, 1.0 );



}
