const float pi = 3.14159265358979;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 of = vec2(0);
    float b = abs(.5-(uv.y+cos(5.*iTime+(uv.x+1.)*pi)/2.));
    if (b<.1) {
        of = vec2(0, -smoothstep(0., 1., sqrt(b*100.)));
    }
    fragColor = texture(iChannel0, uv-of/50.);
}
