void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 res = iResolution;
    vec2 uv = gl_FragCoord.xy / res.xy;
	fragColor=texture(iChannel1,uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    if (iTime<0.5)
    {        
		//fragColor = vec4(uv,1.5*sin(iTime),1.0);
        //fragColor = vec4(fragCoord.xy/iResolution.xy,0.0, 1.0);
        fragColor= texture(iChannel1,uv);
    }else

    if ( length(iMouse.xy-fragCoord.xy)<2. )
    	fragColor=vec4(1.,1.,1,1);
    else
    	fragColor=texture(iChannel0,uv);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 uv00 = mod(vec2(fragCoord.x, fragCoord.y), iResolution.xy) / iResolution.xy;
	vec2 uv01 = mod(vec2(fragCoord.x + 1., fragCoord.y), iResolution.xy) / iResolution.xy;
	vec2 uv02 = mod(vec2(fragCoord.x + 1., fragCoord.y + 1.), iResolution.xy) / iResolution.xy;
	vec2 uv03 = mod(vec2(fragCoord.x, fragCoord.y + 1.), iResolution.xy) / iResolution.xy;
	vec2 uv04 = mod(vec2(fragCoord.x - 1., fragCoord.y + 1.), iResolution.xy) / iResolution.xy;
	vec2 uv05 = mod(vec2(fragCoord.x - 1., fragCoord.y), iResolution.xy) / iResolution.xy;
	vec2 uv06 = mod(vec2(fragCoord.x - 1., fragCoord.y - 1.), iResolution.xy) / iResolution.xy;
	vec2 uv07 = mod(vec2(fragCoord.x, fragCoord.y - 1.), iResolution.xy) / iResolution.xy;
	vec2 uv08 = mod(vec2(fragCoord.x + 1., fragCoord.y - 1.), iResolution.xy) / iResolution.xy;
	vec4 cell = texture(iChannel0, uv00);
	float livecell = 1.;
	float deadcell = 0.;

	vec4 sum = texture(iChannel0, uv01) + texture(iChannel0, uv02) + texture(iChannel0, uv03) + texture(iChannel0, uv04) + texture(iChannel0, uv05) + texture(iChannel0, uv06) + texture(iChannel0, uv07) + texture(iChannel0, uv08);
	for (int i = 0; i < 3; ++i)
	{
		if (cell[i] > 0.5)
		{
			if (sum[i] < 2. || sum[i] > 3.)
				fragColor[i] = deadcell;
			else
				fragColor[i] = livecell;
		}
		else
		{
			if (sum[i] == 3.)
				fragColor[i] = livecell;
			else
				fragColor[i] = deadcell;
		}
	}
	fragColor.w = 1.;
}
