float size = 20.0;
float x = 10.0;
float y = 10.0;
//for checkerboard x and y should be half of size

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy; //for the textures

    vec2 pos = mod(fragCoord.xy,vec2(size));

    if ((pos.x > x) && (pos.y > y)){
    	fragColor=texture(iChannel0,uv);
    }
    if ((pos.x < x) && (pos.y < y)){
    	fragColor=texture(iChannel0,uv);
    }
    if ((pos.x < x) && (pos.y > y)){
    	fragColor=texture(iChannel1,uv);
    }
    if ((pos.x > x) && (pos.y < y)){
    	fragColor=texture(iChannel1,uv);
    }

}
