float createRectangle (vec2 uv, vec2 position, float size, vec2 range)
{    
    float limit = step(position.x + size / range.x, uv.x) - step(position.x, uv.x);
   	limit *= step(position.y + size * range.y, uv.y) - step(position.y, uv.y);

    return limit;
}

