#define PACK_RESOLUTION 256.0
#define PACK_MAX 16.0

vec2 packRGBA(vec4 v)
{
	v = clamp(v, 0.0, PACK_MAX-0.1);
	vec2 pk;
	pk.x = v.r + floor(v.g * PACK_RESOLUTION) * PACK_MAX;
	pk.y = v.b + floor(v.a * PACK_RESOLUTION) * PACK_MAX;
	return pk;
}

vec4 unpackRGBA(vec2 pk)
{
	vec4 re;
	re.g = floor(pk.x / PACK_MAX);
	re.r = pk.x - re.g * PACK_MAX;
	re.g = re.g / PACK_RESOLUTION;

	re.a = floor(pk.y / PACK_MAX);
	re.b = pk.y - re.a * PACK_MAX;
	re.a = re.a / PACK_RESOLUTION;
    return re;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 og = texture(iChannel0, uv).rgb;
	//vec3 og = vec3(uv.x, 1.0, uv.x);

    og *= 15.0;

   	vec2 chan = packRGBA(vec4(og, 1.0));

    vec4 rre = unpackRGBA(chan);
    vec3 re = rre.rgb;

	bool showDiff = iMouse.x > iResolution.x * 0.5;
    bool showOg = iMouse.y > iResolution.y * 0.5;
    vec3 cc;

    if (showDiff)
    	fragColor = vec4(abs(re - og) * 100.0, 1.0), cc = vec3(1.0, 0.0, 0.0);
    else if (showOg)
        fragColor = vec4(og / 15.0, 1.0), cc = vec3(0.0, 1.0, 0.0);
	else
		fragColor = vec4(re / 15.0, 1.0), cc = vec3(0.0, 0.0, 1.0);

    if (uv.x < 0.1 && uv.y < 0.1)
		fragColor = vec4(cc, 1.0);
}
