#define mode 1
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 res = iResolution.xy;
    vec2 uv = fragCoord.xy / res;
    vec2 mouse = iMouse.xy / res;
#if mode == 0 
    //vertical stripes
    vec4 t = texture(iChannel0, vec2(uv.x + mouse.x,clamp(uv.y,  mouse.y-0.005, mouse.y)+mouse.y ));
#endif

#if mode == 1
    //horizontal halfsies
    vec4 t = texture(iChannel0, vec2(clamp(uv.x, mouse.x-0.005, mouse.x), uv.y + mouse.y ));
#endif

#if mode == 2
    //top down curtain
    vec4 t = texture(iChannel0, vec2(uv.x,clamp(uv.y,0.0, mouse.y)));
#endif

#if mode == 3
    //left right curtain
    vec4 t = texture(iChannel0, vec2(clamp(uv.x,0.0, mouse.x), uv.y));
#endif

#if mode == 4
    //horizontal stripes meet in the middle 
    vec4 t = texture(iChannel0, vec2(clamp(uv.x,1.0 - mouse.x, mouse.x)+mouse.y, uv.y));
#endif

#if mode == 5
    //vertical stripes meet in the middle 
    vec4 t = texture(iChannel0, vec2(uv.x, clamp(uv.y,1.0 - mouse.y, mouse.y)+mouse.x));
#endif

    fragColor = t;
}
