bool isGreen(vec3 pixel){
    if (pixel[1] > 0.3) {
        if (pixel[0] + pixel[2] < 0.4)
			return true; 
        else
            return false;
    } else {
        return false;
    }
}

vec3 scanLine(float speed, vec3 composite, vec2 uv) {
    // Noise
    vec3 noise = texture ( iChannel3, uv).xyz;
    vec3 scanLine = vec3(1, 1, 1);
    if ( sin(speed * iTime) > uv.y)
        if ( sin(speed * iTime + 0.1) < uv.y) {
            scanLine = mix(scanLine, noise, 0.5);
            composite = mix(scanLine, composite, 0.5);
        }
    return composite;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


	// Brittney
    float ycoord = (10.0 * sin(2.0 * iTime + 0.03 * fragCoord.x) + fragCoord.y)/iResolution.y;
    float xcoord = uv.x; 
    vec2 p = vec2(xcoord, ycoord);
    vec3 brittney = texture( iChannel0, p ).xyz;
    // background
    vec2 p2 = vec2(uv.x + 0.2 * iTime, uv.y); 
    vec3 composite = texture( iChannel2, p2 ).xyz;
	// Jean
    vec3 jean = texture ( iChannel1, uv ).xyz;

    if (!isGreen(brittney))
        composite = brittney;
    if(!isGreen(jean)){
        composite = vec3(jean[0] * sin(iTime), jean[2], jean[1] );
    }

    composite = scanLine(2.0, composite, uv);
    composite = scanLine(2.5, composite, uv);
    fragColor = vec4(composite, 1);
}
