void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 ps = vec2(1.0) / iResolution.xy;
    vec2 uv = fragCoord * ps;
    vec3 col = texture(iChannel0, uv).rgb;
    float gs = 0.0;

    float mosX = iMouse.x * ps.x;

    vec2 crd = vec2(1.0) - uv;

    float w = mix(0.5, 1.0, mosX);

    if (uv.x < w)
        crd = uv;

    if (crd.y < (1.0 / 4.0)) //-------------------length
        gs = length(col);
    else if (crd.y < (2.0 / 4.0)) //--------------luminosity
        gs = max(col.r, max(col.g, col.b));
    else if (crd.y < (3.0 / 4.0)) //--------------average
        gs = dot(col, vec3(0.333));
    else //---------------------------------------luma
        gs = dot(col, vec3(0.299, 0.587, 0.114));



    col = uv.x > mosX ? vec3(gs) : col;

    fragColor = vec4(col, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 ps = vec2(1.0) / iResolution.xy;
    vec2 uv = fragCoord * ps;
    fragColor = vec4(uv, 0.5 + 0.5 * sin(iTime), 1.0);
    //fragColor = vec4(texture(iChannel0, uv).rgb, 1.0);
}
