void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0, uv).xyz;
    fragColor = vec4(col,1.0);
}

vec2 mouse()
{
    vec2 m = iMouse.xy / iResolution.xy-.5; 
    m.x *= iResolution.x/iResolution.y;
	return m;
}
mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

#define SHADOW_START 0.920


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = 2.0 * vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y; 

    uv2 = uv2 - mouse().xy * 2.0;

    float size = 0.8;

    vec3 col = vec3(0);

    vec3 t = texture(iChannel1, uv).xyz;
    col = t; 

    if(uv2.x < size && uv2.x >-size
       && uv2.y < size && uv2.y > -size)
    {  
        col = vec3(0.0, 0.0, 0.0);

        //uv-=0.5;
        //uv*=rot(sin(iTime*3.0)*0.07);
        //uv+=0.5;

        uv *= 1.08;
    	uv -= mouse().xy*0.9;

    	col = mix(col, texture(iChannel0, uv).xyz, 0.990)*SHADOW_START;

    }




    fragColor = vec4(col,1.0);
}
