float DrawCircle(in vec2 uv, in vec2 center, float radius, float blur)
{
    float dis = length(uv - center);
    float rate = smoothstep(radius, radius, dis);
    return rate;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 red = vec3(1.0, 0.0, 0.0);
    vec3 green = vec3(0.0, 1.0, 0.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv *= 2.0;  //(x,y -> [-1,1])
    uv.x *= iResolution.x / iResolution.y; //x unit equal y unit

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float dis = length(uv);

    float r = 0.5;
    float t = smoothstep(r, r - 0.1, dis);
    vec3 pickCol = red * t + green * (1.0 - t);



    vec3 col = pickCol;
    // Output to screen
    fragColor = vec4(col,1.0);
}
