vec3 fractal(in vec2 p) {
    vec2 c = (p - iResolution.xy*0.5) * 0.005;
    vec2 z0 = vec2(iMouse.x * sin(iTime * 1.0), -iMouse.y * cos(iTime * 1.0)) * 0.05;
    vec2 z = z0;
    for(int i = 0; i < 10; i++) {
        //z0 = x + yi
        //z1 = z0^2 + c
        //   = (x + yi)(x + yi) + (cx + cyi)
        //   = x^2 + 2xyi - y^2 + cx + cyi
        //   = (x^2 - y^2 + cx) + (2xy + cy)i
        float nx = z.x*z.x - z.y*z.y + c.x;
        float ny = 2.0*z.x*z.y + c.y;
        z = vec2(nx, ny);
    }

    //dist(z0, zn)
    vec2 dz = z - z0;
    float d0 = pow(dz.x*dz.x + dz.y*dz.y, 0.75);
    float d1 = pow(dz.x*dz.x + dz.y*dz.y, 0.25);
    //float d2 = pow(dz.x*dz.x + dz.y*dz.y, 0.01);

    vec3 col0 = mix(vec3(0.91, 0.41, 0.0), vec3(0.0, 0.7, 0.71), d0) * 0.75;
    vec3 col1 = mix(vec3(0.6, 0.8, 0.3), vec3(0.4, 0.5, 0.2), d1) * 0.25;
    return clamp(col0 + col1, 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy;// / iResolution.xx;
    vec4 col = vec4(fractal(uv), 1.0);
	fragColor = col;

}
