void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;	// Scale pixel coords to 0..1 range instead

    float pi2 = 3.1415926535 * 2.;
    float waves = 5.;
    float speed = 10.;

    float height= 30.;

    vec3 c = vec3(0.,0.,0.);

    if(sin(uv.x * pi2 * waves * cos(iTime) + (iTime * speed)) > (uv.y-0.5)*height* (sin(iTime))){
        c = vec3(uv.y,sin(iTime),uv.x);
    }

	fragColor   = vec4(c,1.);
}
