float plot(vec2 st, float pct){
  return  smoothstep( pct-0.02, pct, st.y) /
          smoothstep( pct, pct+0.02, st.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 rose = vec3(0.75,0.2,0.88);
    vec3 blue = vec3(0.1,0.55,0.98);
	vec3 jaune = vec3(.244, .235, .66);

    float rx = length((0.0,0.8) * uv.x);
    //float r = rx*ry;
    float c = smoothstep(0.0,uv.y,.1);
    float s = smoothstep(1.0,uv.y,0.55);
    float soleil = smoothstep(0.0,uv.x,0.5)+length((0.0,0.7) * uv);

    vec3 col = (blue*c)+rose*s+soleil*jaune*sin(iTime);
    fragColor = vec4(col,1.0);
}
