void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy *2.-1.;

    float t = iTime;
    vec3 col = vec3(0.);

    float am = .7;
    float frq = 13.;

    float dy = 0.;

    float sh = am * sin(frq*uv.y+t*5.)*.5+.5;

    for (float i=0.; i<3.; i+=1.) {
        dy += abs(5.*sh/sin(uv.y+i+am*sin(uv.x*frq+t+sh)*cos(uv.y+t*.5)));
    }

    col = vec3(dy)*vec3(0.02, 0.002, 0.08*sh);

    fragColor = vec4(vec3(col), 1.);

}
