vec2 N22(vec2 p ){
    vec3 a = fract(p.xyx*vec3(123.34,234.34,345.65));
    a+= dot(a,a+34.45);
    return fract(vec2(a.x*a.y,a.y*a.z));

}
float remap(float a1,float a2,float b1,float b2,float v){
    return abs(a1-v/(a2-v))*b1+abs(a2-v/(a1-v))*b2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
     uv *= 5.;
    float m = 0.;
    float t = iTime*2.;

    float minDist =3.;
    vec3 col = vec3(0);


    int stage = 5;
    for(int i = stage;i>0;i--){
        uv = uv*1.2+t*.2;
        vec2 gv = fract(uv) -.5;
        vec2 id = floor(uv);
        vec3 c = vec3(0);

        for(int y = -1;y<= 1;y++){
            for(int x = -1;x<= 1;x++){
                vec2 offs = vec2(x,y);

                vec2 n =  N22(id + offs+float(i));
                vec2 p = offs + sin(n*t)*.5;
                float d = length(gv-p);

                if(minDist > d){
                    minDist = d;
                }
            }
        }
        c= vec3(1.5*remap(0.,1.,0.7,0.,minDist),1.*remap(0.,1.,0.3,0.,minDist),1.4*minDist);
        col = mix(col,c,.1);
    }

    fragColor = vec4(col,1.0);
}
