// Fork of "Clover mbdev" by mbdev. https://shadertoy.com/view/sdBBRh
// 2022-02-24 09:29:32

// CloverLeafFunction.glsl

// tags: 2d, function, clover, leaf, cloverleaf

float cloverleaf (vec2 p, float r)  // position, radius
{
  p = abs(p) / r;
  return sin(p.x + p.y) - length(p) + 0.01;
}

void mainImage (out vec4 O, in vec2 U)
{
  vec2  R = iResolution.xy ,
        u = (U+U - R) / R.y;

  float s = cloverleaf (u, 0.8+0.2*sin(iTime)),
        t = length(u) - iTime * .5;

  O = min(1.5, s/fwidth(s)) 
    * abs(vec4(cos(t), sin(t), 0, 1));
}
