#define PI 3.14159265
float C (vec2 u, float r) {
    return sin(atan(u.y, u.x)+r+iTime*3.)*.5+.5;
}
void mainImage( out vec4 O, in vec2 I )
{
    vec2 R = iResolution.xy,
         u = (I-.5*R)/R.y;

    float d = length(u);
    float c1 = C(u, 0.);
    float c2 = C(u, PI*.5);
    float c3 = C(u, PI*PI);
    vec3 C = vec3(c1, c2, c3) + step(0.2, d);
    O = vec4(C,1.0);
}
