// start: from https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
// end

void mainImage( out vec4 O, in vec2 u ) {
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.y;
    O = vec4(0.);
    vec2 U1 = round(U * z/2.); 
    if (hash12(U1) > 0.999) {
        O = draw(U, U1 * 2./z);
    }
}
