void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 red = vec3(1.0, vec2(0.0));

    float anim = abs(sin(iTime)) * 0.05;

    vec2 bottom_left = smoothstep(0.1-anim, 0.1+anim, uv);
    vec2 top_right = 1.0-smoothstep(0.9-anim, 0.9+anim, uv);

    vec3 col = bottom_left.x * bottom_left.y * top_right.x * top_right.y * red;

    fragColor = vec4(col,1.0);
}
