void mainImage( out vec4 c, in vec2 f)
{
    vec2 r = iResolution.xy, u = f/r;
    u.x += sin(iTime + u.y * r.y) * tan(iTime * u.y * 100.) * sin(u.y + iTime) * 0.007;
    float rr = texture(iChannel0, u + vec2(sin(iTime + u.y * 200.) * length(u - 0.5) * 0.07 , 0.0)).r;
    float gg = texture(iChannel0, u).g;
    float bb = texture(iChannel0, u + vec2(sin(iTime + u.y * 200. ) * length(u - 0.5) * 0.05 , 0.0)).b;


    c = vec4(rr,gg,bb,1.0) * 1.4;





}

void mainImage( out vec4 c, in vec2 f)
{
    vec2 r = iResolution.xy, u = ((2.0 *f.xy - r))/r.y;
    vec3 rd = normalize(vec3(u, 2.0));
    vec3 ro = vec3(0,0,-10);

    float ll = 0.0;
    float cl = 50.;

    for(int i = 0; i < 255; i++)
    {
        vec3 p = ro + rd * ll;
        p.xz *= mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime));
        vec3 q = abs(p) - vec3(1,2,.001);
        float dd = max(-clamp(sin(iTime + u.y *10.),0.,1.), length(max(q, 0.0)) - min(max(q.x, max(q.y, q.z)), 0.0));

        cl = min(dd, cl);

        ll += dd;

        if(abs(dd) < .01 || ll > 50.)
            break;       
    }

    vec2 dd =vec2(ll,cl);

  // dd.y -= sin(dd.x+ iTime);

    float gg = 1./(1.+ dd.y);
   // gg += abs(cos(iTime + u.y * dd.y)) * 0.1;

    vec3 col = vec3(1, 0, 1) * u.y + vec3(0.4, .2, 1) * (1.-u.y); 

    c = vec4(col * gg, 1);

    c *= step(0.001, dd.x) * sin(iTime + u.y * r.y); 

}
