const float xSize = 0.01;


float point (float f) {
    return xSize/(abs(f)+xSize);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color = texture(iChannel0, uv);

    float progress = 1.0 - mod(iTime*0.5, 1.0);
    float f = abs(uv.y - progress);
    vec4 col = vec4(vec3(point(f)),1.0);


    // Output to screen
    fragColor = mix(color, vec4(1.0) - color, step(progress, uv.y)) + col;
}
