    bool circle(vec2 u, vec2 v, float r)
    {
        float scaleY = iResolution.y / iResolution.x;
         return (u.x - v.x) * (u.x - v.x) + (u.y   - v.y) * (u.y - v.y) * (scaleY * scaleY) < r*r;  // адаптация под размер
    }

