void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float r=0.;
    float l=0.;
    //xn=C*xl*(1-xl)
    float xl=0.5;
    float a=uv.x*4.0;
    for(float i=0.;i<1000.2;i+=1.){
        xl=a*xl*(1.-xl);
    }
    for(float i=0.;i<10000.2;i+=1.){
        if(abs(xl-uv.y)<0.003){
            r=1.;
            l=xl;
            break;
        }
        xl=a*xl*(1.-xl);
    }
    fragColor = vec4(r,l,r,1.0);
}
