float Circle(vec2 uv, vec2 p, float radius, float blur) {
    // Draw circle
    float d = length(uv - p);

    //Smoothstep

    float c = smoothstep(radius,radius-blur,d); 

    return c;
}

float Smiley(vec2 uv, vec2 p, float size){
    // translating cooardinate system
    uv -= p;

    //Scaling coordinate system
    uv /=size;

    float mask = Circle(uv, vec2(0.), .4, .05);

    mask -= Circle(uv, vec2(-.13, .2), .07, 0.01);
    mask -= Circle(uv, vec2(.13, .2), .07, 0.01);

    //Mouth

    float mouth = Circle(uv, vec2(0.), .3, .02);
    mouth -= Circle(uv, vec2(0., 0.1), .3, .02);

    mask -= mouth;

    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Centered Circle
    // uv = uv - vec2(0.5,0.5);
    uv -= .5;

    // Get rid off width strange
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);

    float mask = Smiley(uv, vec2(0., .0), 1.);
    //col = vec3(mouth);
    col = vec3(1., 1., 0.)*mask;

    // Output to screen
    fragColor = vec4(col,1.0);
}
