float Circle(vec2 uv, float r, float blur) {
    float d = length(uv);
    float c = smoothstep(r, r-blur, d);

    return c;
}
vec3 hash32(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv1 = fragCoord.xy / iResolution.xy; 	// 0. <> 1.
    vec2 uv2 = fragCoord.xy / iResolution.xy; 	// 0. <> 1.
    vec2 uv3 = fragCoord.xy / iResolution.xy; 	// 0. <> 1.


    float ldiffx1 = .2;							
 	float ldiffy1 = .17;						
    float mx1 = .5+(sin(iTime*ldiffx1)*.35);
    float my1 = .5+(cos(iTime*ldiffy1)*.25);
    float mr1 = .15+(sin(iTime*.39)*.1);  

    uv1 -= vec2(mx1,my1);							
    uv1.x *= iResolution.x/iResolution.y;

    float mask1 = Circle(uv1, mr1, .1-(mr1*.01));
    float red1 = 0.7 + 0.3*cos(iTime*0.6);
    float green1 = 0.7 + 0.3*cos(iTime*0.6);
    float blue1 = 0.4 + 0.2*cos(iTime*0.46);
    vec3 color1 = vec3(red1,green1,blue1)*mask1;


    float ldiffx2 = .6;	
 	float ldiffy2 = .45;
    float mx2 = .5+(sin(iTime*ldiffx2)*.35);
    float my2 = .5+(cos(iTime*ldiffy2)*.25);
    float mr2 = .15+(sin(iTime*.33)*.11);	
    uv2 -= vec2(mx2, my2);
    uv2.x *= iResolution.x/iResolution.y;

    float mask2 = Circle(uv2, mr2, .1-(mr2*.01));
    float red2 = 0.8 + 0.3*cos(iTime*0.61);
    float green2 = 0.7 + 0.3*cos(iTime*0.61);
    float blue2 = 0.4 + 0.2*cos(iTime*0.41);
    vec3 color2 = vec3(red2,green2,blue2)*mask2;



    float ldiffx3 = .8;	
 	float ldiffy3 = .62;
    float mx3 = .5+(sin(iTime*ldiffx2)*.5);
    float my3 = .5+(cos(iTime*ldiffy2)*.45);
    float mr3 = .15+(sin(iTime*.33)*.13);	

    uv3 -= vec2(mx3,my3);							
    uv3.x *= iResolution.x/iResolution.y;

    float mask3 = Circle(uv3, mr3, .1-(mr3*.01));
    float red3 = 0.2 + 0.6*cos(iTime*0.45);
    float green3 = 0.3 + 0.3*cos(iTime*0.61);
    float blue3 = 0.8 + 0.2*cos(iTime*0.41);
    vec3 color3 = vec3(red3,green3,blue3)*mask3;


    vec3 maincol = vec3(color1 + color2+color3);
    maincol -= (hash32(fragCoord+iTime))*0.15;//noise

    fragColor = vec4(maincol, 1.0);
}
