void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 ro = vec3(uv + iMouse.xy / 100.0f, 0);
    vec3 rd = vec3(uv - 0.5f, 1.0f);
    for (int i = 0;i < 40;i++)
    {
        ro += rd;
        float cell = texture(iChannel0, ro).x;
        if (cell > 0.5f)
        {
            break;
        }
    }
    float col = ro.z * 0.1f;
    fragColor = vec4(vec3(col),1.0);
}
