vec4 load(in int x, in int y) {
	return texelFetch(iChannel0, ivec2(x, y), 0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 mouse = iMouse.xy/iResolution.xy;
    vec2 uv = (fragCoord.xy/iResolution.xy)-0.5;
    float zoom = 2.;//10.-iTime;
    uv.x *= iResolution.x/iResolution.y;
    uv *= zoom;
    //uv -= vec2(0., 0.)+load(1, 0).xy*vec2(iResolution.x/iResolution.y, 1)*zoom;
    vec2 z = vec2(0, 0);
    vec2 c = uv;
    float itr = 1.;
    int m = clamp(iFrame, 1, 100);
    for (int i = 0; i < m; i += 1) {
    	z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y)+c;
        if (length(z) > clamp(iTime, 0.0, 2.0)){
            break;
		}
        itr *= length(z)/2.0;
    }
    vec3 col = vec3(pow((itr*10.0)/5.0, 0.1));
    uv -= load(1, 0).xy*vec2((iResolution.x/iResolution.y), 1);
    fragColor = vec4(col,1.0);
}
