const float pi = 3.14159265358979323846264338327950288419716939937510;

float R(float x) {
    return (cos(x) + 1.) / 3.;
}
float G(float x) {
    return (cos(x - (2. * pi / 3.)) + 1.) / 3.;
}
float B(float x) {
    return (cos(x - (4. * pi / 3.)) + 1.) / 3.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ((fragCoord/iResolution.xy) - 0.5) * 6.;

    //float value = sqrt(uv.x * uv.x + uv.y * uv.y);
    float value = sqrt(uv.x * uv.x * uv.y * uv.y) - iTime;

    const float speed = 1.;

    float myTime = speed * value;

    // Time varying pixel color
    vec3 col = vec3(R(myTime), G(myTime), B(myTime));

    // Output to screen
    fragColor = vec4(col,1.0);
}
