#define PI 3.141592654

mat2 Rot(float angle){
    float s=sin(angle), c=cos(angle);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(1., 0., 0.);
    float f = .02/length(uv);
    float rays = 0.;

    for(float i=1.; i<=4.; i++){
        float r = sin(length(uv)*8.-iTime) * 0.2;
        vec2 p = Rot(r) * uv;
        p *= Rot(PI-PI/8.*i);

        rays += max(0., 1.-abs(p.x*p.y*500.) *i);
        rays += max(0., 1.-fract(sin(abs(p.x*p.y*500.))) *i);
    }

    rays *= smoothstep(.8, 1., 0.9) * .2;
    col *= f + rays;

    fragColor = vec4(col, 1.0);
}
