uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
const uint max32 = 0xffffffffu;
const float PI = 3.14159265359;
uvec2 hash22u(uvec2 n) {  
    n ^= (n.yx << 24);
    n ^= (n.yx >> 1);
    n *= k.xy;
    n ^= (n.yx << 1);
    return n * k.xy;
}
uvec3 hash33u(uvec3 n) {
    n ^= (n.yzx << 24);
    n ^= (n.yzx >> 1);
    n *= k;
    n ^= (n.yzx << 1);
    return n * k;
}
mat2 rot2(float t) {
    return mat2(cos(t), sin(t), -sin(t),  cos(t));
}
float length2(vec2 v){
    return dot(v, v);
}
float length3(vec3 v){
    return dot(v, v);
}
vec2 hash22(vec2 p) {
    uvec2 n = uvec2(p);
    return vec2(hash22u(n)) / vec2(max32) - 0.5;
}
vec3 hash33(vec3 p) {
    uvec3 n = uvec3(p);
    return vec3(hash33u(n)) / vec3(max32) - 0.5;
}
vec4 sort(vec4 list, float v){
    if (list.w < v){
        return list;
    } else {
        vec4 newList = list.wxyz;
        for(int i = 0; i < 4; i ++ ) {
            if (list[i] < v) {
                newList[i] = list[i];
            } else {
                newList[i] = v;
                break;
            }
        }
        return newList;
    }
}
vec4 F4_2d(vec2 p){   
    vec2 n = round(p);
    vec2 f = p - round(p);
    vec4 dist4 = vec4(length2(1.5 - abs(f)));
    vec2 sgn = vec2(1.0);
    vec2 glid = vec2(0.0);
    for(float j = 0.0; j <= 4.0; j ++ ) {
        glid.y += sgn.y * j;
        sgn.y *= -1.0;
        if (abs(glid.y - f.y) - 0.5 > sqrt(dist4.w)){
            continue;
        }
        for(float i = 0.0; i <= 4.0; i ++ ){
            glid.x += sgn.x * i;
            sgn.x *= -1.0;
            vec2 nbd = glid + rot2(iTime) * hash22(n + glid) / sqrt(2.0);
            float dist = length2(nbd -f);
            dist4 = sort(dist4, dist);
        }
        glid.x = 0.0;
    }
    return sqrt(dist4);
}
vec4 F4_3d(vec3 p){   
    vec3 n = round(p);
    vec3 f = p - round(p);
    vec4 dist4 = vec4(length3(1.5 - abs(f)));
    vec3 sgn = vec3(1.0);
    vec3 glid = vec3(0.0);
    for(float k = 0.0; k <= 4.0; k ++ ) {
            glid.z += sgn.z * k;
            sgn.z *= -1.0;
            if (abs(glid.z - f.z) - 0.5 > sqrt(dist4.w)){
                continue;
            }
        for(float j = 0.0; j <= 4.0; j ++ ) {
            glid.y += sgn.y * j;
            sgn.y *= -1.0;
            if (abs(glid.y - f.y) - 0.5 > sqrt(dist4.w)){
                continue;
            }
            for(float i = 0.0; i <= 4.0; i ++ ){
                glid.x += sgn.x * i;
                sgn.x *= -1.0;
                vec3 nbd = glid + hash33(n + glid);
                float dist = length3(nbd - f);
                dist4 = sort(dist4, dist);
            }
            glid.x = 0.0;
        }
        glid.y = 0.0;
    }
    return sqrt(dist4);
}
float cnoise2(vec2 p, vec4 w){
    return abs(dot(F4_2d(p), w));
}
float cnoise3(vec3 p, vec4 w){
    return abs(dot(F4_3d(p), w));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 pos = fragCoord.xy/ iResolution.xx;
    pos *= 10.0;
    pos += iTime;
    int ind = int(3.0 * fragCoord.x / iResolution.x);
    mat3x4 wt = mat3x4(
        vec4(.275),  //left
        vec4(.5, -1.0, 1.4, -.1),  //center
        vec4(-.3, -.5, -1.2, 1.0)  //right
    );
    if(fragCoord.y < 0.5 * iResolution.y){
        fragColor = vec4(cnoise2(pos, wt[ind])) * vec4(0.9, 0.9, 0.7, 1.0);  
    } else {
        fragColor = vec4(cnoise3(vec3(pos, iTime), wt[ind])) * vec4(0.6, 0.8, 0.8, 1.0);  
    }
}
