float pattern(in vec2 coords)
{
    float dist = length(coords);
    dist = abs(dist-0.3);
    float bandwidth = fract(dist*2.-iTime)*0.8 + 0.1;
    return floor(fract(dist*20.+iTime*2.) + 1.-bandwidth);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 coords = (fragCoord-iResolution.xy/2.)/min(iResolution.x, iResolution.y);

    vec2 displace = vec2(sin(iTime), cos(iTime))*0.01*sin(iTime*0.414);
    vec3 col1 = vec3(pattern(coords-displace));
    vec3 col2 = vec3(pattern(coords+displace));

    // Output to screen
    fragColor = vec4(col1.x,col2.y,col2.z,1.0);
}
