float random (float st) {
    return .1 + fract(sin(dot(st, 12.9898)) * 43758.5453123);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    #define num 50.

    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    float level = .5;

    for (float i = 0.; i < num; i++)
    {
        float r1 = random(i);
        float r2 = random(i + .3);
        float r3 = random(i + .5);
        float r4 = random(i + .7);

        vec2 p = sin(iTime * vec2(r1, r2)) / 3.;

        float dist = distance(p, uv);

        level += 8. / num * sin(-iTime * 10. * r3 + 2.0 * r4 * i * dist) * exp(-5.0 * dist);
    }

    fragColor = vec4(level);
}
