#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define r(O,n) pow(sqrt(O.x*O.x+O.y*O.y),n)
void mainImage(out vec4 v4,in vec2 v2){
  v4=vec4(1.0);
  vec2 O=v2,iR=iResolution.xy;    
  O+=iR/8.*vec2(0.0,-0.0)-(iR/2.);
  O*=sin(atan(O.y,O.x)*length(O)/0.51)/length(iR)*4.5;
  v4*=vec4(clamp(r(O,8.)*16.,0.,1.))*r((O),5.0);
  vec3 D3=vec3((1./6.5+5.5/6.5*r(O,1.)),r(O,2.),r(O,3.));
  v4*=2.5-vec4((5./6.+sin(iTime)/6.)*D3,1.5);
  v4.xz*=rot(iTime);
v4=clamp(v4,0.,1.);}
