vec3 pal(float t){return .5+.5*cos(6.28*(1.*t+vec3(0.,.3,.7)));}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 f = vec2(8.,12.);
    vec2 uuv  = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);


  float coord = (floor(uuv.x*f.x )/(f.x*f.y) +floor(uuv.y*f.y )/f.y )/2.;

  coord = 259.*log(1.+ (coord)/700.); // sort of Mel scale
  float t = (texture(iChannel0,fract(vec2(coord))).r);
  vec3 col  = sqrt(vec3(smoothstep(.50,.6,t))*pal(uuv.x));
    fragColor = vec4(col,1.0);
}
