// rework of this GLSL Shader https://glslsandbox.com/e#73033.0 

precision mediump float;


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec3 Color = vec3(0.9, 0.9, 0.9);
	float col = -0.2;
	vec2 a = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x,iResolution.y);
	float t=iTime/21.0;
	for(float i=0.0;i<15.0;i++)
	{
	  float si = sin(t)/3.7;
	  col += 0.19 / abs(length(a*3. + vec2(si*40. , si*20. )) - 0.1);
	}
	fragColor = vec4(vec3(Color * col), 1.0);
}
