void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

#define R 3
#define RC 0.512

float rand(vec2 co)
{
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    if(iFrame <= 30 || iMouse.w > 0.)
    {
        vec2 rc = uv + 0.00001 * vec2(iDate.w + iDate.y, iDate.w + iDate.z);
        if(rand(rc) <= RC + 0.002 && rand(rc) >= RC)
            fragColor = vec4(0.1 + rand(rc + vec2(1, 0)), rand(rc + vec2(2, 0)), rand(rc + vec2(3, 0)), 0.8);
        else
            fragColor = vec4(0, 0, 0, 0);
        return;
    }
    vec4 currentColor4 = texture(iChannel0, uv);
    vec3 currentColor = currentColor4.rgb;
    float cAlpha = currentColor4.a;
    if(cAlpha >= 1.)
    {
        fragColor = currentColor4;
        return;
    }
    vec3 sum;
    float count = 0.;
    for(int dx = -R; dx <= R; dx++)
        for(int dy = -R; dy <= R; dy++)
        {
            if(dx == 0 && dy == 0)
                continue;
            float mul = 1. / length(vec2(dx, dy));
            vec3 col = texture(iChannel0, uv + vec2(dx, dy) / iResolution.xy).rgb;
            if(length(col) > 0.)
            {
                count += mul;
                sum += mul * col;
            }
        }
    if(count > 0.01)
    {
        vec3 avg = sum / count;
        float al = max(0.0000000001, pow(cAlpha, 1.)); 
        avg = currentColor * al + avg * (1. - al);
        float g = avg.g + avg.b - 1. * avg.r + 0.1 * (1. - length(avg) / 3.);
        fragColor = vec4(avg, cAlpha - 0.004 * log(max(0.001, g)));
        return;
    }
    fragColor = vec4(currentColor, 0.);
}
