//reference image0: https://static.projects.iq.harvard.edu/files/styles/os_files_xlarge/public/science-demonstrations/files/thinfilminterference-freeze-wide-2-640x360.jpg
//reference image1: https://static.projects.iq.harvard.edu/files/styles/os_files_xlarge/public/science-demonstrations/files/thinfilminterference-freeze-top-2-640x360.jpg

//source article with reference images: https://sciencedemonstrations.fas.harvard.edu/presentations/thin-film-interference


vec3 sinc(vec3 val)
{
  return sin(val) / val;
}

//reference: https://en.wikipedia.org/wiki/Double-slit_experiment#Classical_wave-optics_formulation
vec3 Fraunhoffer(vec3 lambda, float theta, float d, float b)
{
  float pi = 3.141592f;
  float s = sin(theta);
  vec3 c = cos(pi * d * s / lambda);
  vec3 sc = sinc(pi * b * s / lambda);
  return c * c * sc * sc;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 lambda = vec3(612.0f, 535.0f, 465.0f); //primaries' wavelengths, these values are exact
    float period = 0.1f; //i failed to get anything visually meaningful with large values of this constant. using sin(theta)~theta gives perfectly viable results.
    vec3 col = Fraunhoffer(lambda, uv.x * period, 50.0f / period, 5000.0f / period) * 4.0f; //all constants here are arbitrary and depend on the interferometer geometry
    //col.rgb = vec3(col.r);
    col += vec3(0.1f); //"specular" to match source image better. technically not part of diffraction
    fragColor = vec4(pow(col, vec3(1.0f / 2.2f)), 1.0f); //linear->srgb
}
