#define P 3.14159265
#define R iResolution.xy
void mainImage( out vec4 O, in vec2 I )
{
    vec2 u = (I-.5*R)/R.y;
    float c = 0.;
    vec3 col = vec3(0.);

    float n = 300.;
    for (float i = 0.; i < n; i++) {
        float t = i / n;
        vec2 o = vec2(.7-t*1.3 - sin(t * P * 4.) * sin(t * P + iTime) * .2, -.25 + t*0.45 + cos(t * P * 4.) * cos(t * P - iTime) * .1);
        c = smoothstep(20./R.y, 0., length(u + o) - .05 - t * .1);
        vec3 m = mix(vec3(.8,.2,0.), vec3(1.,1.,0.), t * 4.0);
        m = mix(m, vec3(0.5,0.023,0.9), t);
        col = mix(col, c * m, c);
    }

    O = vec4(col*col+.08,1.0);
}
