void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec4 color = texture( iChannel0, fragCoord/iResolution.xy );
  vec2 r=vec2(fragCoord.xy/iResolution.y);
    vec3 bw=vec3((color.r+color.b+color.g)/3.0);
    vec3 col=vec3(color.r,color.g,color.b);
    vec3 white=vec3(1.0);
    float xMax=iResolution.x/iResolution.y;
    float yMax=1.0;
    float widthLine=xMax/70.0;
    // Boundary
    if(r.x<widthLine){
    col=white;
    }
    else if(r.x>xMax-widthLine){
    col=white;
    }
    else if(r.y<widthLine){
    col=white;
    }
    else if(r.y>1.0-widthLine){
    col=white;
    }
    // Framing X
    else if(r.x<(xMax/5.0)+widthLine/2.0 && r.x>(xMax/5.0)-widthLine/2.0){
    col=white;
    }
    else if(r.x<(2.0*xMax/5.0)+widthLine/2.0 && r.x>(2.0*xMax/5.0)-widthLine/2.0){
    col=white;
    }
    else if(r.x<(3.0*xMax/5.0)+widthLine/2.0 && r.x>(3.0*xMax/5.0)-widthLine/2.0){
    col=white;
    }
    else if(r.x<(4.0*xMax/5.0)+widthLine/2.0 && r.x>(4.0*xMax/5.0)-widthLine/2.0){
    col=white;
    }
    //Framing Y
    else if(r.x<xMax/5.0 && r.y<(yMax/3.0)+widthLine/2.0 && r.y>(yMax/3.0)-widthLine/2.0){
    col=white;
    }
    else if(r.x>xMax/5.0 && r.x < xMax*2.0/5.0  && r.y<(yMax/2.0)+widthLine/2.0 && r.y>(yMax/2.0)-widthLine/2.0){
    col=white;
    }
    else if(r.x>xMax*2.0/5.0 && r.x < xMax*3.0/5.0  && r.y<(yMax/4.0)+widthLine/2.0 && r.y>(yMax/4.0)-widthLine/2.0){
    col=white;
    }
    else if(r.x>xMax*2.0/5.0 && r.x < xMax*3.0/5.0  && r.y<(yMax/1.6)+widthLine/2.0 && r.y>(yMax/1.6)-widthLine/2.0){
    col=white;
    }
    else if(r.x>xMax*2.0/5.0 && r.x < xMax*3.0/5.0  && r.y<(yMax/1.6)+widthLine/2.0 && r.y>(yMax/1.6)-widthLine/2.0){
    col=white;
    }
    else if(r.x>xMax*3.0/5.0 && r.x < xMax*4.0/5.0  && r.y<(yMax/2.7)+widthLine/2.0 && r.y>(yMax/2.7)-widthLine/2.0){
    col=white;
    }
    else if(r.x>xMax*4.0/5.0 && r.x < xMax*5.0/5.0  && r.y<(yMax/1.8)+widthLine/2.0 && r.y>(yMax/1.8)-widthLine/2.0){
    col=white;
    }
    fragColor=vec4(col, 1.0);
    //Note code can be optimized further
}
