#define PI 3.141592653589793

float oneWave(float x) {
    return (sign(x+1.0) / 2.0 + 0.5) * (-sign(x - 1.0) / 2.0 + 0.5) * (0.5 + 0.5 * cos(x * PI));
}

float rand(float x,float seed) {
    return fract(tan(x + seed) * 300.0);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord-iResolution.xy) / iResolution.y;
    vec2 mu = (2.0 * iMouse.xy-iResolution.xy) / iResolution.y;

    vec2 p1 = vec2(
        -1.0 + rand(floor(iTime), 1.0) * 2.0,
        -1.0 + rand(floor(iTime), 2.0) * 2.0
    );

    vec2 p2 = vec2(
        -1.0 + rand(floor(iTime+0.5), 3.0) * 2.0,
        -1.0 + rand(floor(iTime+0.5), 4.0) * 2.0
    );

    vec3 col = vec3(0.0, 0.2, 1.0);

    float est = acos(cos((iTime * 2.0 + 1.0) * PI)) / PI;

    vec2 route = uv + (p1 + (p2 - p1) * est);

    float jt = iTime * 2.0 * PI;

    float animat = max(0.5 + 0.5 * sin(jt), 0.5 + 0.5 * cos((jt) + PI / 2.0));

    float arcMob = oneWave((length(route) + animat * -0.20) * 60.0);

    col += arcMob;

    fragColor = vec4(col,1.0);
}
