            fragColor = vec4(0.0);
        }
    }
    else{
        fragColor = texture(iChannel0, uv);
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    ivec2 fragI = ivec2(int(fragCoord.x), int(fragCoord.y));

    vec4 col = texelFetch(iChannel1, fragI, 0);




    if(iMouse.z > 0. || iFrame == 0){
        float closest = 1.5;

        int num = int(float(maxTessels) * (iMouse.x / iResolution.x));//int(float(nTessels) * (sin(iTime) + 1.)) + 1;

        if(iFrame == 0){
            num = maxTessels / 4;
        }


        for(int i = 0; i < num; i++){

            ivec2 numCoords = ivec2(i % int(iResolution.x), int(floor(float(i) / iResolution.x)));


            vec4 p = texelFetch(iChannel0, numCoords, 0);


            if(distance(uv, p.xy) < closest){
                closest = distance(uv, p.xy);
                col = p;//vec4(1. / (closest * 100.0));
            }
        }
    }


    fragColor = col;


}
