#define s 1./16.
#define T iTime
float L (vec2 uv, float x, float y) {
    // return (uv.x > s*x && uv.x < s*(x+1.) && uv.y > s*y && uv.y < s*(y+1.)) ? texture(iChannel0, uv).r : 0.;
    return (uv.x > s*x && uv.x < s*(x+1.) && uv.y > s*y && uv.y < s*(y+1.)) ? (.5 - texture(iChannel0, uv).a) * .0625 * iResolution.y + 0.5 : 0.;
}
void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = I/iResolution.xy;
    uv = uv*vec2(1., s*9.8) + vec2(.006*sin(30.*uv.y+T*2.)-s*1.5, .003*sin(40.*uv.x+T*2.)+s*4.5);
    float c = L(uv + vec2(-s, s), 3., 10.) // S
    + L(uv + vec2(0., s), 5., 10.) // U
    + L(uv + vec2(-s*6., s), 0., 10.) // P
    + L(uv + vec2(-s*6., s*2.), 1., 11.) // A
    + L(uv + vec2(0., s*2.), 8., 11.); // H
    O = vec4(vec3(1.,c,c),1.0);
}
