const int sampleRadius = 20; //the radius of the square to sample from (bascially blurriness)
const float threshold = 0.05; //the max delta for it to blend

float avgCol(vec3 col){
    return (col.x + col.y + col.z)/3.0;
}

vec3 fetch(ivec2 coord){
    return texelFetch(iChannel0, coord, 0).xyz;
}

//returns color put in if the delta of the current pixel's color and the color to test is below a threshold
void checkCol(in vec3 testingCol, in vec3 col, inout vec3 toAvg, inout vec3 successCount){
    vec3 delta = vec3(abs(col.x-testingCol.x),abs(col.y-testingCol.y),abs(col.z-testingCol.z));
    if (delta.x<threshold && delta.y<threshold && delta.z<threshold){
        toAvg += testingCol;
        successCount ++;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 iCoord = ivec2(fragCoord*2.0);
    vec3 col = fetch(iCoord);
    col = col;
    vec3 toAvg;
    vec3 successCount;
    vec3 testingCol;

    for (int x = sampleRadius/2; x > -sampleRadius/2; x--){
        for (int y = sampleRadius/2; y > -sampleRadius/2; y--){
            if (x == 0 && y == 0){continue;}
            testingCol = fetch(ivec2(iCoord.x+x, iCoord.y+y));
            checkCol(testingCol,col,toAvg, successCount);
        }
    }

    if (successCount.x != 0.0 && successCount.y != 0.0 && successCount.z != 0.0){ 
        toAvg /= successCount; 
        col = toAvg;
    }

    fragColor = vec4(col,0);
}
