void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    bool animating = true;        
    float rate = 1.5;
    float brightness = .5;
    float vertScale = .5;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    //offset time -plug into Time property in local environment and multiply by rate.

    if (animating == true){
        uv.y += iTime * rate;
    }else{
        uv.y += (iResolution.y/iResolution.x + 1.0);
    }

    uv.y *= vertScale;


    //triangle wave
    float triGrad = abs( mod( (.5 * uv.y), 1. ) + mod( (.5 * uv.y), -1.0)) ; 


    vec4 col = vec4(triGrad * brightness);

    // Output to screen
    fragColor = vec4(col);
}
