float fbm(vec2 uv){
    float f=0.;
    float s=15.;
    float o=8.;
    for(float i=o;i>=0.;i--){
        uv+=sin(iTime*(1./o)+(i*(1.57/o)))*.2;
        f+=texture(iChannel0,uv/((-s/o)*i+s)).r*(1./(o-1.));
    }
    return f;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float a=iTime/5.;
    mat2 m=mat2(cos(a),-sin(a),sin(a),cos(a));
    uv=m*uv*2.*(.8+.2*sin(iTime/2.));
    vec3 col = vec3(fbm(uv));
    col*=.6+.4*sin(vec3(.3,.5,.7)*(iTime+length(uv)*3.14));
    fragColor = vec4(col,1.0);
}
