vec4 getParam(int p) {
    return texelFetch(iChannel0, ivec2(p, 0), 0);
}

vec2 cmul(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y,
                a.x * b.y + a.y * b.x);
}

vec2 cdiv(vec2 a, vec2 b) {
    float s = 1.0 / length(b);
    b.y = -b.y;
    return cmul(a * s, b * s);
}

vec2 term(vec2 x, int n) {
    vec2 r = getParam(n).rg;
    return x - r;
}

vec2 f(vec2 x) {
    vec2 fx = vec2(1.0, 0.0);
    for (int i = 0; i < nroots; ++i) {
        fx = cmul(fx, term(x, i));
    }
    return fx;
}

vec2 df(vec2 x) {
    vec2 right[nroots];
    right[nroots - 1] = vec2(1.0, 0.0);
    for (int i = nroots - 1; i > 0; --i) {
        right[i - 1] = cmul(right[i], term(x, i));
    }
    vec2 dfx = vec2(0.0);
    vec2 left = vec2(1.0, 0.0);
    for (int i = 0; i < nroots; ++i) {
        dfx += cmul(left, right[i]);
        left = cmul(left, term(x, i));
    }
    return dfx;
}


int find_nearest(vec2 uv) {
    int nearest = 0;
    for (int i = 1; i < nroots; ++i) {
        if (length(term(uv, i)) < length(term(uv, nearest))) {
            nearest = i;
        }
    }
    return nearest;
}

vec3 add_roots(vec3 col, vec2 uv) {
    // TODO: figure out proper pixel-size-aware smoothstep params.
    for (int i = 0; i < nroots; ++i) {
        float r = abs(0.02 - length(term(uv, i)));
        col = mix(vec3(0.0), col, smoothstep(0.0, 0.01, r));
    }
    return col;
}

vec3 add_grid(vec3 col, vec2 uv) {
    uv = fract(uv) * 5.0;
    float h = (1.0 - smoothstep(0.0, 0.05, abs(uv.y - round(uv.y))));
    float v = (1.0 - smoothstep(0.0, 0.05, abs(uv.x - round(uv.x))));
    h *= uv.y < 0.5 || uv.y > 4.5 ? 0.75 : 0.25;
    v *= uv.x < 0.5 || uv.x > 4.5 ? 0.75 : 0.25;

    col = mix(col, vec3(1.0), max(h, v));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#if 0
    vec2 mid = iResolution.xy * 0.5;
    vec2 s = 2.0 / mid.xx;
    vec2 uv = (fragCoord - mid) * s;
#else
    vec2 uv = fragToPlane(fragCoord, getParam(P_RESCALE));
#endif
    vec2 uv0 = uv;


    for (int i = 0; i < max_iter; ++i) {
        uv -= cdiv(f(uv), df(uv));
    }


    int nearest = find_nearest(uv);
    vec3 col = palette[nearest % palette.length()];

    col = add_grid(col, uv0);
    col = add_roots(col, uv0);

    fragColor = vec4(col,1.0);
}

vec4 getParam(int p) {
    return texelFetch(iChannel0, ivec2(p, 0), 0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 outData = vec4(0.0);
    vec4 m = iMouse;
    bool down = m.z > 0.0;
    bool click = m.w > 0.0;
    m = fragToPlane(abs(m), getParam(P_RESCALE));

    int root = int(fragCoord.x);
    if (root < nroots) {
        outData.xy = getParam(root).xy;
        if (iFrame < 1) {
            outData.xy = roots[root];
        }
        if (down && !click) {
            int grabbed = int(getParam(P_GRABBED).r * float(nroots));
            if (root == grabbed) {
                outData.xy = m.xy;
            }
        }
    } else {
        if (root == P_GRABBED) {
            int grabbed = int(getParam(P_GRABBED).r * float(nroots));
            if (click) {
                grabbed = 0;
                float d = distance(getParam(0).xy, m.xy);
                for (int i = 1; i < nroots; ++i) {
                    float dd = distance(getParam(i).xy, m.xy);
                    if (dd < d) {
                        grabbed = i;
                        d = dd;
                    }
                }
            }
            outData.x = (float(grabbed) + 0.5) / float(nroots);
        } else if (root == P_RESCALE) {
            outData = getParam(P_RESCALE);
//            if (iFrame < 1) {
                outData = vec4(iResolution.xy * 0.5,
                               vec2(6.2) / iResolution.xx);
//            }
        }
    }

    fragColor = outData;
}

vec2 roots[] = vec2[](
    vec2( 0.00, 1.00),
    vec2( 0.00,-1.00),
    vec2( 0.66,-0.52),
    vec2( 0.66, 0.52),
    vec2(-1.32, 0.00)
#if 0
    ,vec2( 1.20, 1.20)
    ,vec2(-1.20, 1.20)
    ,vec2( 1.20,-1.20)
    ,vec2(-1.20,-1.20)
#endif
);

const int nroots = roots.length();
const int P_GRABBED = nroots + 0;
const int P_RESCALE = nroots + 1;

const int max_iter = 6;

vec3 palette[] = vec3[](
    vec3(.1, .7, .7),
    vec3(.3, .7, .3),
    vec3(.3, .3, .6),
    vec3(.4, .2, .4),
    vec3(.5, .7, 1.),
    vec3(.2, .8, .7),
    vec3(.1, .8, .3),
    vec3(.4, .4, .8),
    vec3(.2, .2, .5),
    vec3(.1, .5, 1.)
);



vec2 fragToPlane(vec2 fc, vec4 scale)
{
    return (fc - scale.xy) * scale.zw;
}

vec4 fragToPlane(vec4 mc, vec4 scale)
{
    return (mc - scale.xyxy) * scale.zwzw;
}
