float Circle(vec2 _uv, float _radius, float _blurSize, vec2 _position)
{
    _uv+=_position;

    _uv.x *= iResolution.x/iResolution.y;
    float d = length(_uv) ;
    float c = d;
    float r = _radius;
    float s = _blurSize;
    c = smoothstep(r,r+s,c);

    return 1.0-c;
}

float SmileFace(vec2 _uv, vec2 p, float size)
{
    vec2 uvPos = _uv + p ;
    uvPos *= 1.0-(size * 0.1);
    float eyes = Circle(uvPos, 0.02,0.005, vec2(0.05,-0.05)) + Circle(uvPos, 0.02,0.005, vec2(-0.05,-0.05));
    float mouth = Circle(uvPos, 0.14,0.005, vec2(0.00,0.015)) * (1.0- Circle(uvPos, 0.16,0.005, vec2(0.00,-0.035)));
    float head = Circle(uvPos, 0.2,0.01, vec2(0.0,0.0)) * (1.0 - eyes) * (1.0 - mouth);

    return head;
}


float Band(float t, float start, float end, float blur)
{
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);

    //float square = smoothstep(0.2,0.4,_uv.x);

    return step1*step2;
}

float Rect(vec2 _uv, float left, float right, float bottom, float top, float blur)
{
    //_uv.x *= iResolution.x/iResolution.y;
    float band1 = Band(_uv.x, left, right, blur);
    float band2 = Band(_uv.y, bottom, top, blur);

    return band1*band2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;


    vec3 headColored = SmileFace(uv, (-iMouse.xy / iResolution.xy), 3.0) * vec3(1.0,1.0,0);

    vec3 allTogetherNow = (Rect(uv, 0.2, .8 ,-0.7, 0.2, 0.75)*0.6 *vec3(1.0,0.0,0)) +headColored;

    // Output to screen
    fragColor = vec4(vec3(clamp(allTogetherNow, 0.0, 1.0)),.5);
}
