#define S(v) smoothstep(0.,1., abs( fract(v) *2. - 1. ) ).y   // smooth line

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 14.*( u+u - R ) / R.y;
    O = vec4(   S( U )                                        // ropes
              * S( U * mat2(3,1, abs(U.y)>6.5 ? -1 : 1 ,3) )  // threads
            );
    O = sqrt(O);                                              // to sRGB
}
