void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord/iResolution.y) - 1.0;

    vec2 uv0 = uv*0.5;
    vec2 uv1 = (uv*0.2) + vec2(-0.5,0.5);
    vec2 uv2 = uv*0.5;

    vec3 col0 = texture(iChannel0, uv0).rrr;
    vec3 col1 = texture(iChannel1, uv1).rgb;
    vec3 col2 = texture(iChannel2, uv2).rgb;

    float t = 2.0*abs(fract(iTime*0.15)-0.5);
    t = smoothstep( 0.1, 0.9, t);

    // Set color to minimum between mask value and time
    // below t value, display #2, above display #1
    float ratio = smoothstep( t-0.0001, t+0.0001, col0.r );
    vec3 col = mix(col1, col2, ratio);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
